import React from 'react';

const BuyNow = ({ selectedItems, onBuy }) => {
  return (
    <div className="container" style={{ backgroundColor: 'floralwhite', width: '70%' }}>
      <h2 className="pb-3 fs-4">Checkout</h2>
      <table className="table table-borderless" style={{ fontFamily: 'Arial', backgroundColor: '#f7f7f7' }}>
        <thead>
          <tr>
            <th className="col-4">Item</th>
            <th className="col-4">Price</th>
            <th className="col-4">Quantity</th>
          </tr>
        </thead>
        <tbody>
          {selectedItems.map((item, index) => (
            <tr key={index}>
              <td className="col-4">
                <span>{item.name}</span>
              </td>
              <td className="col-4">
                <span>&#8377;{item.price}</span>
              </td>
              <td className="col-4">
                <span>{item.quantity}</span>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <div className="text-center">
        <button
          type="button"
          className="btn btn-success"
          onClick={onBuy}
        >
          Place Order
        </button>
      </div>
    </div>
  );
};

export default BuyNow;
