import React, { useState } from 'react';

const BuyNow = ({ selectedItems }) => {
    const [orderPlaced, setOrderPlaced] = useState(false);

    const handlePlaceOrder = () => {
        // You can implement the logic to place the order here
        // You can use an API or any other method to submit the order
        // Once the order is placed, you can set orderPlaced to true
        setOrderPlaced(true);
    };

    return (
        <div className="container">
            <h2>Selected Items</h2>
            <form>
                {selectedItems.map((item, index) => (
                    <div key={index} className="mb-3">
                        <img src={item.imageUrl} alt={item.name} className="img-thumbnail" />
                        <label className="form-label">Item: {item.name}</label>
                        <label className="form-label">Price: &#8377;{item.price}</label>
                        <label className="form-label">Quantity: {item.quantity}</label>
                    </div>
                ))}
                {orderPlaced ? (
                    <p>Order Placed Successfully!</p>
                ) : (
                    <button type="button" className="btn btn-primary" onClick={handlePlaceOrder}>
                        Purchase
                    </button>
                )}
            </form>
        </div>
    );
};

export default BuyNow;
